perspectives.style:
@controls {
  bottom {
    cluster {
      by: "";
      as: buttons;
      multiple: true;
      default: "";
    }
  }
}

@settings {
  font-size: 150;
  font-weight: 700;
  background-color: #ffffff;
  element-text-align: center;
  layout-preset: hairball;
  text-overflow: wrap 10;
  connection-curvature: 0.38;
  template: systems;
  layout: force;
  element-color: scale("degree", neon2);
  element-size: 49;
  connection-size: 1;
  element-scale: scale("degree", 0.5, 3);
}

element {
  color: #eeeeee;
  stroke-width: 4px;
  opacity: 1;
}

loop {
  font-color: #333333;
  font-weight: Black;
  font-size: 100;
}

connection {
  scale: 20;
}

connection["connection type"="+"] {
  color: #4AC9CD;
  opacity: 0.5;
}

connection["connection type"="-"] {
  color: #8B0512;
  opacity: 0.5;
}

/* elements:  */
["Element Type"="Source"] {
  scale: scale("degree", 5, 6);
  shadow-opacity: 0.1;
  shadow-size: 2;
  shadow-color: #333333;
}
